@props([
	'id' => 'assam_map' . md5(Str::random(8)),
	'color' => '#0095A8', // '#1bac9c',
	'data' => [],
	'width' => 977.951,
	'height' => 728.75,
	'toolbar'=> true,
	'svgClasses' => 'w-full h-full'
])

@once
	@push('scripts')
		<script src="https://unpkg.com/@popperjs/core@2"></script>
		<script src="https://unpkg.com/tippy.js@6"></script>
	@endpush
@endonce

@push('scripts-footer')
	<script>
		function hexToRGB(hex, alpha) {
			const r = parseInt(hex.slice(1, 3), 16);
			const g = parseInt(hex.slice(3, 5), 16);
			const b = parseInt(hex.slice(5, 7), 16);

			return (alpha) ? `rgba(${r}, ${g}, ${b}, ${alpha})` : `rgba(${r}, ${g}, ${b})`;
		}

		function numFormatter(num) {
            if (num > 999 && num < 1000000) {
                return (num / 1000).toFixed() + 'K';
            } else if (num > 1000000) {
                return (num / 1000000).toFixed() + 'M';
            } else if (num < 900) {
                return num.toFixed() + '+';
            }
        }

		function convertSpacesToUnderscores(inputString) {
			// Use a regular expression to replace all spaces with underscores
			return inputString.replace(/ /g, '_');
		}

        function generateRange(min = 0, max, buckets = 5) {
            const a = (max - min) / buckets;
            let ranges = []

            for (let i = min; i <= max; i = i + a) {
                ranges.push(i);
            }

            return ranges.splice(1);
        }

		document.addEventListener('livewire:load', function () {
			let datasets = @json($data);
			let svgMap = @json($id);
			let mapColor = @json($color);
			let toolbar =  @json($toolbar);

			let maxValueFromDataset = Math.max.apply(Math, datasets.map((data) => parseFloat(data.value)));

			if (datasets.length) {
                let maxValueFromDataset = Math.max.apply(Math, datasets.map((data) => parseFloat(data.value)));

                // Range Statistics
                if (toolbar) {
                    document.getElementById(`js-dataRange-${svgMap}`).innerHTML = generateRange(0, maxValueFromDataset, 5)
                        .map((
                            item) => {
                            const percentage = (parseInt(item) / maxValueFromDataset).toFixed(2);
                            return `<div class="w-16 md:w-20"><span class="h-2 md:h-3 block" style="background-color: ${hexToRGB(mapColor, percentage)}"></span><span class="block text-gray-600 text-sm">${numFormatter(parseFloat(item))}</span></div>`;
                        }).join("");
                }

				datasets.forEach(dataset => {
					// console.log(dataset.name);
					const percentage = (parseInt(dataset.value) / maxValueFromDataset).toFixed(2);
					const districtName = convertSpacesToUnderscores(dataset.name);
					let targetSvgPath = document.querySelector(`#${districtName} path`);
					targetSvgPath.setAttribute('fill', hexToRGB(mapColor, percentage));
	
					// Mouseover
					targetSvgPath.addEventListener("mouseover", event => {
						targetSvgPath.style.opacity = 0.5; 
					});
					targetSvgPath.addEventListener("mouseout", event => {
						targetSvgPath.style.opacity = 1;
					});
	
					// Add Tooltip
					tippy(`#${districtName}`, {
						content: `
							<span class="font-semibold block mb-1 text-xs">${dataset.name}: ${dataset.value}</span>
						`,
						maxWidth: '200px',
						allowHTML: true
					});
				});
			}
		});
	</script>	
@endpush

